import { useState, useRef , } from 'react'
import { makeBuyOrder } from '../store/interactions.js'
import { useSelector , useDispatch } from 'react-redux'


const Order = () => {
const [isBuy, setIsBuy] = useState(true)
const [amount, setAmount] = useState(0)
const [price, setPrice] = useState(0)
const dispatch = useDispatch()
const buyRef = useRef(null)
const sellRef = useRef(null)

const buyHandler = (e) => {
  const provider = useSelector(state => state.provider.connection)
  const exchange = useSelector(state => state.exchange.contract)
  const tokens = useSelector(state => state.tokens.contracts)
  const order = {amount, price}

  e.preventDefault()
  makeBuyOrder(provider, exchange, tokens, order, dispatch)
  setAmount(0)
  setPrice(0)
}
const sellHandler = (e) => {
  e.preventDefault()
  console.log(price)
  setAmount(0)
  setPrice(0)
}

const tabHandler = (e) => {
    if(e.target.className !== buyRef.current.className) {
      e.target.className = 'tab tab--active'
      buyRef.current.className = 'tab'
      setIsBuy(false)
    } else {
      e.target.className = 'tab tab--active'
      sellRef.current.className = 'tab'
      setIsBuy(true) 
    }
  }

  return (
    <div className="component exchange__orders">
      <div className='component__header flex-between'>
        <h2>New Order</h2>
        <div className='tabs'>
          <button onClick={ tabHandler} ref={buyRef} className='tab tab--active'>Buy</button>
          <button onClick={ tabHandler} ref={sellRef}  className='tab'>Sell</button>
        </div>
      </div>

      <form onSubmit={ isBuy ? buyHandler : sellHandler }>
        {isBuy ? (
          <label htmlFor="amount">Buy Amount</label> 
        ):(
          <label htmlFor="amount">Sell Amount</label>
        )}

        <input 
        type="text" 
        id='amount' 
        placeholder='0.0000' 
        value={amount === 0 ? '' : amount}
        onChange={(e) => setAmount(e.target.value)}/>

        {isBuy ? (
          <label htmlFor="price">Buy Price</label> 
        ):(
          <label htmlFor="price">Sell Price</label>
        )}

        <input 
        type="text" 
        id='price' 
        placeholder='0.0000' 
        value={price === 0 ? '' : price}
        onChange={(e) => setPrice(e.target.value)}/>

        <button className='button button--filled' type='submit'>
          {isBuy ? (
            <span>Buy Order</span>
          ):(
            <span>Sell Order</span>
          )}
        </button>
      </form>
    </div>
  );
}

export default Order;
