import { useState } from 'react'

const Order = () => {
const [isBuy, setIsBuy] = useState(true)
const [amount, setAmount] = useState(0)
const [price, setPrice] = useState(0)

const buyHandler = (e) => {
  console.log('buyHandler..')
}
const sellHandler = (e) => {
  console.log('sellHandler..')
}

  return (
    <div className="component exchange__orders">
      <div className='component__header flex-between'>
        <h2>New Order</h2>
        <div className='tabs'>
          <button className='tab tab--active'>Buy</button>
          <button className='tab'>Sell</button>
        </div>
      </div>

      <form onSubmit={ isBuy ? buyHandler : sellHandler }>
        <input type="text" id='amount' placeholder='0.0000' onChange={(e) => setAmount(e.target.value)}/>

        <input type="text" id='price' placeholder='0.0000' onChange={(e) => setPrice(e.target.value)}/>

        <button className='button button--filled' type='submit'>
          {isBuy ? (
            <span>Buy Order</span>
          ):(
            <span>Sell Order</span>
          )}
        </button>
      </form>
    </div>
  );
}

export default Order;
