import config from '../config.json'
import { useSelector } from 'react-redux'

const Network = useSelector(state => state.provider.connection)
const DARK = config[Network].DARK
const mETH = config[Network].mETH

const Markets = () => {
  return(
    <div className='component exchange__markets'>
      <div className='component__header'>
      	<h2>Select Market</h2>
      </div>
      	<select name="markets" id="markets">
    	  		<option value={`${DARK.address},${mETH.address}`}>DARK / mETH</option>
      		</select>
      <hr />
    </div>
  )
}

export default Markets;
